(257,	89,	33,	NULL,	'Arthour !… Pas changer assiette pour fromage !',	0.5,	1),
(258,	89,	33,	NULL,	'Couhillère, couhillère, couhillère ! Ave Cesar ! (lache une caisse)',	0.5,	1),
